// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.7;

import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";
import "hardhat/console.sol";

contract WalletProxy  {

    struct Slot {
        address value;
    }

    bytes32 constant _IMPLEMENTATION_SLOT = keccak256("proxy.slot");

    constructor(address _logic, address owner) payable {
        (bool success,) = _logic.delegatecall(abi.encodeWithSignature("init(address)", owner));
        require(success);
        StorageSlot.getAddressSlot(_IMPLEMENTATION_SLOT).value = _logic;
    }
    //    ERC1967Proxy(_logic, abi.encodeWithSignature("init(address)", owner)) {}

    function update(address newLogic) external {
        //        require(msg.sender==address (this), "update: only through wallet");
        //        _upgradeTo(newLogic);
    }

    function _setImplementation(address value) internal {
        getAddressSlot(_IMPLEMENTATION_SLOT).value = value;
    }

    function _implementation() internal  view returns (address) {
        return getAddressSlot(_IMPLEMENTATION_SLOT).value;
    }

    function getAddressSlot(bytes32 slot) internal pure returns (Slot storage ret) {
        assembly {
            ret.slot := slot
        }
    }

    function _delegate(address implementation) internal virtual {
        assembly {
        // Copy msg.data. We take full control of memory in this inline assembly
        // block because it will not return to Solidity code. We overwrite the
        // Solidity scratch pad at memory position 0.
            calldatacopy(0, 0, calldatasize())

        // Call the implementation.
        // out and outsize are 0 because we don't know the size yet.
            let result := delegatecall(gas(), implementation, 0, calldatasize(), 0, 0)

        // Copy the returned data.
            returndatacopy(0, 0, returndatasize())

            switch result
            // delegatecall returns 0 on error.
            case 0 {
                revert(0, returndatasize())
            }
            default {
                return(0, returndatasize())
            }
        }
    }

    /**
     * @dev Delegates the current call to the address returned by `_implementation()`.
     *
     * This function does not return to its internall call site, it will return directly to the external caller.
     */
    function _fallback() internal virtual {
        _delegate(_implementation());
    }

    /**
     * @dev Fallback function that delegates calls to the address returned by `_implementation()`. Will run if no other
     * function in the contract matches the call data.
     */
    fallback() external payable virtual {
        _fallback();
    }

    /**
     * @dev Fallback function that delegates calls to the address returned by `_implementation()`. Will run if call data
     * is empty.
     */
    receive() external payable virtual {
        _fallback();
    }

}

